import java.nio.file.*

// Get FlowFile
def flowFile = session.get()
if (!flowFile) return

// Get attributes
def sourceDir = flowFile.getAttribute('absolute.path')
def baseFilename = flowFile.getAttribute('base.filename')

// Extract directory path
def sourceDirectory = sourceDir.substring(0, sourceDir.lastIndexOf('/'))

// Log for debugging
log.info("Processing runtime file deletion for: ${baseFilename}")
log.info("Source directory: ${sourceDirectory}")

// Construct ONLY main runtime file path
// (Audit file does NOT have its own runtime file!)
def mainRuntimeFile = sourceDirectory + '/' + baseFilename + '.txt.runtime'

def deletedCount = 0
def errors = []

try {
    // Delete ONLY main runtime file
    def mainPath = Paths.get(mainRuntimeFile)
    if (Files.exists(mainPath)) {
        Files.delete(mainPath)
        log.info("âœ“ Deleted runtime file: ${mainRuntimeFile}")
        deletedCount++
    } else {
        log.warn("Runtime file not found: ${mainRuntimeFile}")
    }
} catch (Exception e) {
    log.error("Failed to delete runtime file: ${e.message}")
    errors.add("Runtime: ${e.message}")
}

// Add attributes to FlowFile
flowFile = session.putAttribute(flowFile, 'runtime.files.deleted', deletedCount.toString())
if (errors.size() > 0) {
    flowFile = session.putAttribute(flowFile, 'runtime.deletion.errors', errors.join('; '))
}

// Always transfer to success (non-critical failure)
session.transfer(flowFile, REL_SUCCESS)

import java.nio.file.*

def flowFile = session.get()
if (!flowFile) return

def basePath = flowFile.getAttribute('absolute.path')
def baseFilename = flowFile.getAttribute('base.filename')

// Find all files matching base pattern
def pattern = "${baseFilename}*"
def dir = Paths.get(basePath)

def relatedFiles = []
try {
    Files.newDirectoryStream(dir, pattern).each { path ->
        def filename = path.fileName.toString()
        // SKIP .runtime files - they are just markers!
        if (!filename.contains('.runtime')) {
            relatedFiles << filename
        }
    }

    if (relatedFiles.isEmpty()) {
        session.transfer(flowFile, REL_FAILURE)
        return
    }

} catch(Exception e) {
    log.error("Error finding files: ${e.message}")
    session.transfer(flowFile, REL_FAILURE)
    return
}

// Create separate FlowFile for each related file (excluding .runtime)
relatedFiles.each { filename ->
    def child = session.create(flowFile)
    child = session.putAttribute(child, 'filename', filename)
    child = session.putAttribute(child, 'absolute.path', basePath)
    child = session.putAttribute(child, 'path', basePath)
    session.transfer(child, REL_SUCCESS)
}

// Remove parent FlowFile
session.remove(flowFile)

