import org.apache.nifi.processor.exception.ProcessException

log.info("=== AirPlus Attribute Builder Script (FlowFile-driven) Started ===")

// === CONFIG (edit per-processor if needed) ===
final String folderPath = 'C:/Z/ReportFiles_AirPlus'   // hardcoded directory
final String attributeKey = "airplus_regex"
final String filenameAttr = "filename"
final String filepathAttr = "filepath"

def trigger = session.get()
if (trigger == null) {
    // No incoming FlowFile => do nothing (processor will not run)
    return
}

try {
    def dir = new File(folderPath)
    if (!dir.exists() || !dir.isDirectory()) {
        log.error("Directory not found: ${folderPath}. No FlowFiles will be created.")
        session.transfer(trigger, REL_FAILURE)
        return
    }

    def allFiles = dir.listFiles()?.findAll { it != null && it.isFile() } ?: []
    if (!allFiles) {
        log.warn("Directory is empty: ${folderPath}. Nothing to process.")
        session.transfer(trigger, REL_SUCCESS)
        return
    }

    // --- BUILD RUNTIME PATTERNS (your exact logic) ---
    def runtimes = allFiles.findAll { it.name?.toLowerCase()?.contains('runtime') }

    def patterns = []
    runtimes.each { file ->
        try {
            def matcher = (file.name =~ /(\d{6})/)
            def dates = []
            while (matcher.find()) {
                dates << matcher.group(1)
            }
            if (dates.size() >= 2) {
                patterns << ".*${dates[0]}.*${dates[1]}.*"
            }
        } catch (Exception e) {
            log.warn("Skipping runtime file ${file?.name} due to parse error: ${e.message}")
        }
    }

    def combinedRegex = patterns ? "(${patterns.join('|')})" : ""

    if (!combinedRegex) {
        log.error("No patterns extracted; cache will NOT be updated and NO FlowFile will be transferred.")
        session.transfer(trigger, REL_FAILURE)
        return
    }

    log.info("Combined regex built: ${combinedRegex}")

    // --- CREATE FLOWFILES FOR NON-RUNTIME FILES ---
    def nonRuntimeFiles = allFiles.findAll { !(it.name?.toLowerCase()?.contains('runtime')) }

    if (!nonRuntimeFiles) {
        log.warn("No non-runtime files found to create FlowFiles.")
        session.transfer(trigger, REL_SUCCESS)
        return
    }

    nonRuntimeFiles.each { file ->
        try {
            def ff = session.create(trigger)                       // child FlowFile (links provenance to trigger)
            ff = session.putAttribute(ff, attributeKey, combinedRegex)
            ff = session.putAttribute(ff, filenameAttr, file.name)
            ff = session.putAttribute(ff, filepathAttr, file.absolutePath)

            // Optionally: write file content into ff (commented out â€” enable if you want content)
            // ff = session.write(ff, { inputStream, outputStream ->
            //     file.withInputStream { fis ->
            //         byte[] buffer = new byte[8192]
            //         int read = 0
            //         while ((read = fis.read(buffer)) != -1) {
            //             outputStream.write(buffer, 0, read)
            //         }
            //     }
            // } as StreamCallback)

            session.transfer(ff, REL_SUCCESS)
            session.getProvenanceReporter().receive(ff, "file://${file.getAbsolutePath()}")
            log.info("Transferred FlowFile for: ${file.name}")

            // OPTIONAL: move processed file to archive to avoid re-processing (uncomment + edit)
            // def archived = new File(file.parentFile, "archive")
            // if (!archived.exists()) archived.mkdirs()
            // file.renameTo(new File(archived, file.name))
        } catch (Exception pfErr) {
            log.error("Failed to create FlowFile for ${file?.name}: ${pfErr.message}", pfErr)
            // continue processing other files; optionally create a failure flowfile
        }
    }

    // transfer the trigger FlowFile after creating children
    session.transfer(trigger, REL_SUCCESS)
} catch (Throwable t) {
    log.error("ExecuteScript failed: ${t.message}", t)
    try {
        session.transfer(trigger, REL_FAILURE)
    } catch (Exception e) {
        log.error("Failed to route to failure: ${e.message}", e)
    }
    throw new ProcessException(t)
}

import java.io.File

try {
    def ff = session.get()
    if (!ff) return

    def basefilename = ff.getAttribute('basefilename')
    def sourceDir = ff.getAttribute('sourceDir') ?: 'C:\\Users\\karthikn\\cox-rapidscale\\apache-nifi\\nifi\\temp\\tcn_test\\'

    File directory = new File(sourceDir)

    if (!directory.exists()) {
        ff = session.putAttribute(ff, 'error', 'Directory not found: ' + sourceDir)
        session.transfer(ff, REL_FAILURE)
        return
    }

    def relatedFiles = directory.listFiles()
        ?.findAll { it.isFile() && it.getName().toLowerCase().contains(basefilename.toLowerCase()) && !it.getName().contains('.runtime') }
        ?.collect { it.getAbsolutePath() }
        ?: []

    if (relatedFiles.isEmpty()) {
        session.transfer(ff, REL_FAILURE)
        return
    }

    // Store all related files for reference
    ff = session.putAttribute(ff, 'relatedFiles', relatedFiles.join(','))
    ff = session.putAttribute(ff, 'relatedFilesCount', relatedFiles.size().toString())

    // Create ONE FlowFile for EACH related file
    // This ensures all files (main + audit) are processed through FetchFile and PutFile
    relatedFiles.eachWithIndex { filePath, index ->
        def newFF = session.create(ff)
        def fileName = new File(filePath).getName()

        // Set filename attribute for this specific file
        newFF = session.putAttribute(newFF, 'filename', fileName)
        newFF = session.putAttribute(newFF, 'fileIndex', (index + 1).toString())
        newFF = session.putAttribute(newFF, 'totalFiles', relatedFiles.size().toString())

        session.transfer(newFF, REL_SUCCESS)
    }

    // Remove the original FlowFile (it's no longer needed)
    session.remove(ff)

} catch (Exception e) {
    def ff = session.get()
    if (ff) {
        ff = session.putAttribute(ff, 'error', e.toString())
        session.transfer(ff, REL_FAILURE)
    }
}
